tinymce.PluginManager.add('selectimg', function (editor) {

    function selectLocalImages() {
        
        editor.windowManager.open(
            {
                title: "选择图片", //    对话框的标题
                file: '/static/tinymce/plugins/selectimg/selectimg.html', //    放置对话框内容的HTML文件
                width: 500, //    对话框宽度
                height: 400, //    对话框高度
                inline: 1, //    Whether to use modal dialog instead of separate browser window.
            }
        );
        /* editor.focus();
        editor.selection.setContent(editor.dom.createHTML('img', { src: "src" })); */
    }


    editor.addButton('selectimg', {
        icon: 'image',
        image: '/static/tinymce/plugins/selectimg/icon.png',
        tooltip: '从服务器选择图片',
        onclick: selectLocalImages,
        ppk: 'cici'
    });

});